import { MatIconModule } from "@angular/material/icon";
import { Component, OnInit, computed, inject } from "@angular/core";
import { CommonModule, NgTemplateOutlet } from "@angular/common";
import { MatCardModule } from "@angular/material/card";
import { MatButtonModule } from "@angular/material/button";
import { MatTabsModule } from "@angular/material/tabs";
import { ToDoListSignalsService } from "src/app/services/to-do-list-signals.service";
import { TodoKeyLocalStorage } from "src/app/models/enum/toDoKeyLocalStorage";

@Component({
  selector: "app-to-do-card",
  standalone: true,
  imports: [
    CommonModule,
    NgTemplateOutlet,
    MatCardModule,
    MatButtonModule,
    MatIconModule,
    MatTabsModule,
  ],
  templateUrl: "./to-do-card.component.html",
  styleUrls: [],
})
export class ToDoCardComponent implements OnInit {
  private toDoListSignalsService = inject(ToDoListSignalsService);
  private toDoState = this.toDoListSignalsService.toDoState;
  public toDoListData = computed(() => this.toDoState());

  ngOnInit(): void {
    this.getDatasLocalStorage();
    console.log(this.toDoListData());
  }

  getDatasLocalStorage(): void {
    const toDoData = localStorage.getItem(
      TodoKeyLocalStorage.ToDoList
    ) as string;

    toDoData && this.toDoState.set(JSON.parse(toDoData));
  }

  public saveInLocalStorage(): void {
    this.toDoListSignalsService.saveInLocalStorage();
  }

  public deleteAllLocalStorage(): void {
    this.toDoListSignalsService.deleteLocalStorage();
  }
}
<mat-tab-group color="primary">
  <button
    *ngIf="toDoListData().length > 0"
    mat-raised-button
    type="button"
    class="btn_excluir_todo"
    color="warn"
  >
    Excluir Todo
  </button>

  <mat-tab label="Em Andamento">
    <div *ngFor="let item of toDoListData()">
      <mat-card class="todo_card" *ngIf="item?.done === false">
        <mat-card-header>
          <mat-card-title class="todo_card_title">
            {{ item.title }}
          </mat-card-title>

          <mat-card-subtitle class="todo_card_subtitle">
            {{ item.description }}
          </mat-card-subtitle>
        </mat-card-header>

        <mat-card-actions class="todo_card_btn_actions" align="end">
          <button
            class="header_btn check-btn"
            mat-icon-button
            aria-label="Concluir Tarefa"
          >
            <mat-icon class="">done_all</mat-icon>
          </button>

          <button
            class="header_btn check-btn edit-task"
            mat-icon-button
            aria-label="Editar Task"
          >
            <mat-icon>edit</mat-icon>
          </button>

          <button
            class="header_btn check-btn todo_form_delete_btn"
            mat-icon-button
            aria-label="Deletar task"
          >
            <mat-icon>delete_forever</mat-icon>
          </button>
        </mat-card-actions>
      </mat-card>
    </div>
  </mat-tab>
  <mat-tab label="Finalizado">
    <div *ngFor="let item of toDoListData()">
      <mat-card class="todo_card" *ngIf="item?.done === true">
        <mat-card-header>
          <mat-card-title class="todo_card_title">
            {{ item.title }}
          </mat-card-title>

          <mat-card-subtitle class="todo_card_subtitle">
            {{ item.description }}
          </mat-card-subtitle>
        </mat-card-header>

        <mat-card-actions class="todo_card_btn_actions" align="end">
          <button
            class="header_btn check-btn"
            mat-icon-button
            aria-label="Concluir Tarefa"
          >
            <mat-icon class="">done_all</mat-icon>
          </button>

          <button
            class="header_btn check-btn todo_form_delete_btn"
            mat-icon-button
            aria-label="Deletar task"
          >
            <mat-icon>delete_forever</mat-icon>
          </button>
        </mat-card-actions>
      </mat-card>
    </div>
  </mat-tab>
</mat-tab-group>
